import React from 'react';


const PunBubble={
    width: 300,
    background: "black",
    borderRadius: "50%",
    padding: "3rem 4rem",
    margin: "1.5rem", 
    textAlign: "center"
}

const hidden = {
    display: 'none'
}

const container = {
    display: "flex",
    flexFlow: "row wrap",
    justifyContent: "center",
    alignItems: "center",
    height: 280,
}

function Puns(props){
    return(
        <div className="quote-container" style={container}>
            <h3 className="setup" id="setup" style={props.newPun ? PunBubble : hidden}>
                {props.setup}
            </h3>
            <h3
                className="punchline"
                id="punchline"
                style={props.newPun ? hidden : PunBubble}>
                {props.punAns}
            </h3>
        </div>
    );
}

export default Puns;
