import React from 'react';
import Puns from './Puns';
import Button from './Button';

class PunBox extends React.Component {
    state = {
        setup: '',
        punline: '',
        newPun: false,
    }

    componentDidMount = () => {
        this.getNewPun();
    }

    getNewPun = () => {
        fetch("https://dad-jokes.p.rapidapi.com/random/jokes", {
            "method": "GET",
            "headers": {
                "x-rapidapi-host": "dad-jokes.p.rapidapi.com",
                "x-rapidapi-key": "3bd40f8211msh2c334ee10705e82p184456jsn2ce91118d94f"
            }
        })
        .then(response => {
            return response.json();
        })

        .then(data => {
            this.setState({
                setup: data[0].setup,
                newPun: true,
            })
        })
        .catch(err => {
            console.log(err);
        });
    }
    getPunline = () => {
        this.setState({
            newPun: false
        });
    }

    render(){
        return(
            <div className='PunBox' id='PunBox' style={{width: '100%'}}>
                <Puns
                    setup={this.state.setup}
                    punchline={this.state.punchline}
                    newPun={this.state.newPun}
                    />
                <Button
                    setup={this.state.setup}
                    punchline={this.state.punchline}
                    newPun={this.state.newPun}
                    />
            </div>
        )
    }
}

export default PunBox;
